#!/usr/bin/env node
/**
 * Perplexica Standalone Launcher
 * Generated by build_perplexica.js
 */

const path = require('path');
const { spawn } = require('child_process');

// Set environment variables
process.env.NODE_ENV = process.env.NODE_ENV || 'production';
process.env.PORT = process.env.PORT || '3000';
process.env.HOSTNAME = process.env.HOSTNAME || '127.0.0.1';

// Add current directory to module paths
const appDir = path.dirname(__filename);
require('module')._nodeModulePaths.unshift(path.join(appDir, 'node_modules'));

// Start the Next.js server
const serverPath = path.join(__dirname, 'server.js');
require(serverPath);
